import React from 'react';
import './TimeDisplay.css';

function TimeDisplay({ accumulatedTime, isEarning, isSpending, doubleSpeed }) {
  // Format seconds into HH:MM:SS
  const formatTime = (seconds) => {
    const hours = Math.floor(seconds / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);
    const secs = seconds % 60;

    return [
      hours.toString().padStart(2, '0'),
      minutes.toString().padStart(2, '0'),
      secs.toString().padStart(2, '0')
    ].join(':');
  };

  // Determine the status class for styling
  let statusClass = isEarning ? 'earning' : isSpending ? 'spending' : '';
  if (doubleSpeed) {
    statusClass += ' double-speed';
  }

  // Determine status text
  let statusText = 'Timer stopped';
  if (isEarning) {
    statusText = doubleSpeed ? 'Earning time at 2x speed!' : 'Earning time...';
  } else if (isSpending) {
    statusText = doubleSpeed ? 'Spending time at 2x speed!' : 'Spending time...';
  } else if (doubleSpeed) {
    statusText = 'Timer stopped (2x mode active)';
  }

  return (
    <div className={`time-display ${statusClass}`}>
      <h2>Accumulated Time</h2>
      <div className="timer">{formatTime(accumulatedTime)}</div>
      <div className="status">{statusText}</div>
      {doubleSpeed && !isEarning && !isSpending && (
        <div className="speed-indicator">2x Mode Active</div>
      )}
    </div>
  );
}

export default TimeDisplay;
