import React from 'react';
import './TimeDisplay.css';

function TimeDisplay({ accumulatedTime, isEarning, isSpending }) {
  // Format seconds into HH:MM:SS
  const formatTime = (seconds) => {
    const hours = Math.floor(seconds / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);
    const secs = seconds % 60;

    return [
      hours.toString().padStart(2, '0'),
      minutes.toString().padStart(2, '0'),
      secs.toString().padStart(2, '0')
    ].join(':');
  };

  // Determine the status class for styling
  const statusClass = isEarning ? 'earning' : isSpending ? 'spending' : '';

  return (
    <div className={`time-display ${statusClass}`}>
      <h2>Accumulated Time</h2>
      <div className="timer">{formatTime(accumulatedTime)}</div>
      <div className="status">
        {isEarning && 'Earning time...'}
        {isSpending && 'Spending time...'}
        {!isEarning && !isSpending && 'Timer stopped'}
      </div>
    </div>
  );
}

export default TimeDisplay;
